mapper.select:
SELECT 

FROM base_route_main a

WHERE a.id = #{id}

SELECT 

FROM base_route_main a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.code = #{code}


		AND a.product_id = #{product.id}


		AND product.code = #{product.code}


		AND a.version = #{version}


		AND a.enable = #{enable}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM base_route_main a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
