mapper.select:
SELECT 

FROM base_site a

WHERE a.id = #{id}

SELECT 

FROM base_site a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.id = #{id}


		AND a.code = #{code}


		AND a.factory_id = #{factory.id}


		AND a.workshop_id = #{workshop.id}


		AND a.line_id = #{line.id}


		AND a.name LIKE
		    '%'||#{name}||'%'
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM base_site a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE base_site SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
