mapper.select:
SELECT 

FROM base_unit a

WHERE a.id = #{id}

SELECT
FROM base_unit a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.name LIKE '%'||#{name}||'%' '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM base_unit a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE base_unit SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
