mapper.select:
SELECT 

FROM base_workshop a

WHERE a.id = #{id}

SELECT 

FROM base_workshop a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.factorry_id = #{factory.id}


		AND a.id = #{id}


		AND a.name LIKE
		    '%'||#{name}||'%'
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM base_workshop a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE base_workshop SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
