mapper.select:
SELECT

FROM business_baogao_order a

WHERE a.bgcode = #{code} limit 1;

SELECT 

FROM business_baogao_order a

WHERE a.id = #{id}

SELECT 

FROM business_baogao_order a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.ordercode = #{ordercode}


		AND a.plancode = #{plancode}


		AND a.dept = #{dept}


		AND a.bgcode = #{bgcode}


		AND a.cinvcode = #{cinvcode}


		AND a.cinvname like concat('%',#{cinvname},'%')


		AND a.cinvstd like concat('%',#{cinvstd},'%')


		and a.end_date = #{enddate}


		and a.start_date = #{startdate}


		and a.isprint = #{isprint}




		ORDER BY ${page.orderBy}


		ORDER BY a.start_date asc

SELECT 

FROM business_baogao_order a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_baogao_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
