mapper.select:
SELECT 

FROM business_chuku_lingliao a

WHERE a.id = #{id}

SELECT 

FROM business_chuku_lingliao a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.code = #{code}


		AND a.sccode = #{sccode}


		AND a.cinvcode = #{cinvcode}


		AND a.cinvname LIKE
		    '%'||#{cinvname}||'%'
			'%'||#{cinvname}||'%'
			'%'+#{cinvname}+'%'
			concat('%',#{cinvname},'%')


		AND a.cinvstd = #{cinvstd}


		AND a.cangku = #{ck.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_chuku_lingliao a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_chuku_lingliao SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
