mapper.select:
SELECT 

FROM business_dispatch a

WHERE a.id = #{id}

SELECT 

FROM business_dispatch a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.code = #{code}


		AND a.u8code = #{u8code}


		AND a.dept = #{dept.id}


		AND a.customer = #{customer.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_dispatch a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_dispatch SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
