mapper.select:
SELECT 

FROM business_om_modetail a

WHERE a.id = #{id}

SELECT 

FROM business_om_modetail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.moid = #{mo.id}


		AND b.code = #{mo.code}


		AND a.cinvcode = #{cinvcode}


		AND a.cinvname like concat('%',#{cinvname},'%')


		AND a.cinvstd like concat('%',#{cinvstd},'%')


		AND a.printstatus = #{printstatus}




		ORDER BY ${page.orderBy}


		ORDER BY b.code asc, a.no ASC

SELECT 

FROM business_om_modetail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM business_om_modetail


		WHERE id = #{id}


		WHERE moid = #{mo.id}

UPDATE business_om_modetail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE moid = #{mo.id}
