mapper.select:
SELECT 

FROM business_pandian a

WHERE a.id = #{id}

SELECT 

FROM business_pandian a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.code = #{code}


		AND a.ddate BETWEEN #{beginDdate} AND #{endDdate}


		AND a.ck = #{ck.id}


		AND a.hw = #{hw.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_pandian a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_pandian SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
