mapper.select:
SELECT 

FROM business_product a

WHERE a.id = #{id}

SELECT 

FROM business_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.code = #{code}


		AND a.name LIKE
		    '%'||#{name}||'%'
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type_id = #{type.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
