mapper.select:
SELECT 

FROM business_product a

WHERE a.id = #{id}

SELECT
FROM business_product a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND (b.id = #{type.id} OR b.parent_ids LIKE '%,'||#{type.id}||',%') '%,'||#{type.id}||',%') CONCAT('%,', #{type.id}, ',%')) '%'+#{type.id}+'%')
AND a.code = #{code}
AND a.name LIKE '%'||#{name}||'%' '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 

FROM business_product a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM business_product


		WHERE id = #{id}


		WHERE type_id = #{type.id}

UPDATE business_product SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE type_id = #{type.id}
