mapper.select:
SELECT 

FROM business_product_type a

WHERE a.id = #{id}

SELECT
FROM business_product_type a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.parent_id = #{parent.id}
AND a.code = #{code}
AND a.name LIKE '%'||#{name}||'%' '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
ORDER BY a.sort ASC

SELECT 

FROM business_product_type a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM business_product_type a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE business_product_type SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
