mapper.select:
SELECT 

FROM business_route a

WHERE a.id = #{id}

SELECT 

FROM business_route a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.product_id = #{product.id}


		AND a.site_id = #{site.id}


		and a.version = #{version}


		and a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.no asc

SELECT 

FROM business_route a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_route SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
