mapper.select:
select a.id as "id",a.no AS "no",a.cinv_code AS "cinv.code",a.cinv_name AS "cinvname",a.std AS "std",a.unit AS "unit",a.num AS "num",a.start_date AS "startdate",
	   a.end_date AS "enddate",p.code as "p.code",a.dept AS "dept.id",dept.name AS "dept.name"
FROM business_shengchan_dingdan_mingxi a
left join business_shengchan_dingdan p on p.id = a.pid
LEFT JOIN sys_office dept ON dept.id = a.dept

	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND p.code = #{p.code}


		AND a.dept = #{dept.id}




		ORDER BY ${page.orderBy}


		ORDER BY p.code asc,a.no ASC

SELECT 

FROM business_shengchan_dingdan_mingxi a

WHERE a.id = #{id}

SELECT 

FROM business_shengchan_dingdan_mingxi a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.pid = #{p.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.no ASC

SELECT 

FROM business_shengchan_dingdan_mingxi a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM business_shengchan_dingdan_mingxi


		WHERE id = #{id}


		WHERE pid = #{p.id}

UPDATE business_shengchan_dingdan_mingxi SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE pid = #{p.id}
