mapper.select:
SELECT 

FROM qiyewx_dept a

WHERE a.id = #{id}

SELECT 

FROM qiyewx_dept a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM qiyewx_dept a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE qiyewx_dept SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
